lat=#{lat}, lng=#{lng}, useTime=#{useTime}, parkingFree=#{parkingFree}, note=#{note}, 
limityn=#{limitYn}, limitDetail=#{limitDetail},
busiId=#{busiId}, bnm=#{bnm}, busiNm=#{busiNm},busiCall=#{busiCall}
WHERE statId=#{statId}
